--card owner: saikufunction c11227797.initial_effect(c)	--pendulum summon	aux.EnablePendulumAttribute(c)	--spsummon	local e1=Effect.CreateEffect(c)	e1:SetDescription(aux.Stringid(11227797,0))	e1:SetCategory(CATEGORY_SPECIAL_SUMMON)	e1:SetType(EFFECT_TYPE_IGNITION)	e1:SetRange(LOCATION_PZONE)	e1:SetCountLimit(1,11227797)	e1:SetCondition(c11227797.spcon)	e1:SetTarget(c11227797.sptg)	e1:SetOperation(c11227797.spop)	c:RegisterEffect(e1)	--destroy	local e2=Effect.CreateEffect(c)	e2:SetDescription(aux.Stringid(11227797,1))	e2:SetCategory(CATEGORY_DESTROY)	e2:SetType(EFFECT_TYPE_IGNITION)	e2:SetProperty(EFFECT_FLAG_CARD_TARGET)	e2:SetCountLimit(1)	e2:SetRange(LOCATION_PZONE)	e2:SetCost(c11227797.cost)	e2:SetTarget(c11227797.target)	e2:SetOperation(c11227797.operation)	c:RegisterEffect(e2)	--positionchnge	local e3=Effect.CreateEffect(c)	e3:SetCategory(CATEGORY_POSITION)	e3:SetType(EFFECT_TYPE_SINGLE+EFFECT_TYPE_TRIGGER_O)	e3:SetProperty(EFFECT_FLAG_CARD_TARGET+EFFECT_FLAG_DAMAGE_STEP+EFFECT_FLAG_DELAY)	e3:SetCode(EVENT_SUMMON_SUCCESS)	e3:SetTarget(c11227797.postg)	e3:SetOperation(c11227797.posop)	c:RegisterEffect(e3)	local e4=e3:Clone()	e4:SetCode(EVENT_SPSUMMON_SUCCESS)	c:RegisterEffect(e4)	--splimitPZoneMZone	local e5=Effect.CreateEffect(c)	e5:SetType(EFFECT_TYPE_FIELD)	e5:SetCode(EFFECT_CANNOT_SPECIAL_SUMMON)	e5:SetProperty(EFFECT_FLAG_PLAYER_TARGET)	e5:SetRange(LOCATION_PZONE+LOCATION_MZONE)       e5:SetTargetRange(1,0)	e5:SetTarget(c11227797.splimit)	c:RegisterEffect(e5)endfunction c11227797.spcon(e,tp,eg,ep,ev,re,r,rp)	returnDuel.GetFieldGroupCount(tp,LOCATION_MZONE,0)==0endfunctionc11227797.sptg(e,tp,eg,ep,ev,re,r,rp,chk)	local c=e:GetHandler()	if chk==0 then return Duel.GetLocationCount(tp,LOCATION_MZONE)>0		andc:IsCanBeSpecialSummoned(e,0,tp,false,false)end	Duel.SetOperationInfo(0,CATEGORY_SPECIAL_SUMMON,c,1,0,0)endfunction c11227797.spop(e,tp,eg,ep,ev,re,r,rp)	local c=e:GetHandler()	if c:IsRelateToEffect(e) then		Duel.SpecialSummon(c,0,tp,tp,false,false,POS_FACEUP)	endendfunction c11227797.atkcon(e)	local c=e:GetHandler()	local tp=e:GetHandlerPlayer()	returnDuel.GetMatchingGroupCount(nil,tp,LOCATION_MZONE,0,c)==0endfunction c11227797.costfilter(c)	return c:IsFaceup() and c:IsSetCard(0x26E7)endfunction c11227797.cost(e,tp,eg,ep,ev,re,r,rp,chk)	if chk==0 then return Duel.CheckReleaseGroup(tp,c11227797.costfilter,1,e:GetHandler()) end	local sg=Duel.SelectReleaseGroup(tp,c11227797.costfilter,1,1,e:GetHandler())	Duel.Release(sg,REASON_COST)endfunction c11227797.target(e,tp,eg,ep,ev,re,r,rp,chk,chkc)	if chkc then return chkc:IsControler(1-tp) and chkc:IsOnField() end	if chk==0 then return Duel.IsExistingTarget(aux.TRUE,tp,0,LOCATION_ONFIELD,1,nil) end	Duel.Hint(HINT_SELECTMSG,tp,HINTMSG_DESTROY)	local g=Duel.SelectTarget(tp,aux.TRUE,tp,0,LOCATION_ONFIELD,1,1,nil)	Duel.SetOperationInfo(0,CATEGORY_DESTROY,g,1,0,0)endfunction c11227797.operation(e,tp,eg,ep,ev,re,r,rp)	local tc=Duel.GetFirstTarget()	if tc and tc:IsRelateToEffect(e) then		Duel.Destroy(tc,REASON_EFFECT)	endendfunction c11227797.filter(c)	return c:IsCanChangePosition()endfunction c11227797.postg(e,tp,eg,ep,ev,re,r,rp,chk,chkc)	if chkc then return chkc:IsLocation(LOCATION_MZONE) and c11227797.filter(chkc) end	if chk==0 then return Duel.IsExistingTarget(c11227797.filter,tp,LOCATION_MZONE,LOCATION_MZONE,1,nil) end	Duel.Hint(HINT_SELECTMSG,tp,HINTMSG_POSCHANGE)	local g=Duel.SelectTarget(tp,c11227797.filter,tp,LOCATION_MZONE,LOCATION_MZONE,1,1,nil)	Duel.SetOperationInfo(0,CATEGORY_POSITION,g,1,0,0)endfunction c11227797.posop(e,tp,eg,ep,ev,re,r,rp)	local tc=Duel.GetFirstTarget()	if tc:IsRelateToEffect(e) then		Duel.ChangePosition(tc,POS_FACEUP_DEFENSE,POS_FACEUP_ATTACK,POS_FACEUP_ATTACK,POS_FACEUP_ATTACK)	endendfunctionc11227797.splimit(e,c,tp,sumtp,sumpos)	return not c:IsSetCard(0x26E7)end